import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({

  state: {
    admin: {
      id : 0,
      username : '游客',
      phone : 'xxx',
      token : 'xxx'
    }
  },
  getters: {
  },
  // 通过当前属性中定义的函数修改共享变量，必须是同步操作
  mutations: {
    setAdmin(state, adminPar){
      state.admin.id = adminPar.id;
      state.admin.username = adminPar.username;
      state.admin.phone = adminPar.phone;
      state.admin.token = adminPar.token;
    }
  },
  // 通过actions调用mutations，在actions中可以进行异步操作
  // axios异步请求的回调函数调用mutation中的函数
  actions: {
    adminLogin(context, loginPar){
      axios({
				url: '/api/admin/admin/login',
				method: 'post',
				data: { // data表示通过请求体传参
					username: loginPar.username,
					password: loginPar.password
				}
			}).then(res => { // 成功回调函数再发请求
				console.log(res.data.code)
				if(res.data.code == 1){ // 登录成功
					console.log(res.data.data.token)
					this.$message("登录成功")
          context.commit('setAdmin', res.data.data)
				}else{
					console.log(res.data.msg)
					this.$message(res.data.msg)
				}

			})
    }

  },
  modules: {
  }
})
